import ListBookTikiProduct from './ListBookTikiProduct'
import React from 'react'
import {
    BrowserRouter,
    Route,
    Switch,
    Link
} from "react-router-dom";

class ListBookTiki extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            listBook: ''
        }
    }

    componentDidMount() {
        fetch('http://localhost:3600/books')
            .then(response => {
                return response.json()
            })
            .then(data => {
                // this.setState({ data })
                console.log(data)
                let str = data.map((row, index) => {
                    return (
                        <tr>
                            <th scope="row" style={{ textAlign: "center", verticalAlign: "middle" }}>{index + 1}</th>
                            <td> <Link to="/listBookTikiProduct">{row.Name}</Link></td>
                        </tr>
                    )
                });
                this.setState({ listBook: str })
            });
    }

    render() {
        return (
            <BrowserRouter>
                <div>
                    <table class="table">
                        <thead>
                            <tr>
                                <th scope="col" style={{ textAlign: "center" }}>STT</th>
                                <th scope="col">Name</th>
                            </tr>
                        </thead>
                        <tbody>
                            {this.state.listBook}
                        </tbody>
                    </table>
                </div>
                <Route exact path="/listBookTikiProduct" render={ ( ) => (<div> <ListBookTikiProduct/> </div>) } />
            </BrowserRouter>
        )
    }
}

export default ListBookTiki;
