import React from 'react'

class ListBookTiki extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            listBook: ''
        }
    }

    componentDidMount() {
        fetch('http://localhost:3600/books')
            .then(response => {
                return response.json()
            })
            .then(data => {
                // this.setState({ data })
                console.log(data)
                let str = data.map ((row, index) => {
                    return(
                        <tr><th scope="row">{index}</th><td>{row.ID}</td><td>{row.Name}</td></tr>
                    )
                });
                this.setState({listBook: str})
            });
    }

    render() {
        return (
            <div>
                <table class="table">
                    <thead>
                        <tr>
                            <th scope="col">#</th>
                            <th scope="col">ID</th>
                            <th scope="col">Name</th>
                        </tr>
                    </thead>
                    <tbody>
                        {this.state.listBook}
                    </tbody>
                </table>
            </div>


        )
    }
}

export default ListBookTiki;
