import ListBookTikiProduct from './ListBookTikiProduct'
import React from 'react'
import FontAwesome from 'react-fontawesome'
import {
    Link
} from 'react-router-dom';

import 'font-awesome/css/font-awesome.min.css';

class ListBookTiki extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            listBook: ''
        }
    }

    componentDidMount() {
        fetch('http://localhost:3600/books')
            .then(response => {
                return response.json()
            })
            .then(data => {
                // this.setState({ data })
                console.log(data)
                let str = data.map((row, index) => {
                    return (
                        <tr>
                            <th scope="row" style={{ textAlign: "center", verticalAlign: "middle" }}>{index + 1}</th>
                            <td> <Link to={"/listBookTikiProduct/" + row.ID + "/" + row.Name}>{row.Name}</Link></td>
                            <td> 
                                <Link to={"/updateBook"}>
                                    <i className="fa fa-sun-o"/>
                                </Link>
                            </td>
                            <td> 
                                <Link to={"/deleteBook"}>
                                    <FontAwesome
                                        className='super-crazy-colors'
                                        name='trash-alt'
                                        size='2x'
                                        spin
                                        style={{ textShadow: '0 1px 0 rgba(0, 0, 0, 0.1)' }}
                                    />
                                </Link>
                            </td>
                        </tr>
                    )
                });
                this.setState({ listBook: str })
            });
    }

    render() {
        return (
            <div>
                <table class="table">
                    <thead>
                        <tr>
                            <th scope="col" style={{ textAlign: "center" }}>STT</th>
                            <th scope="col">Name</th>
                            <th scope="col">Update</th>
                            <th scope="col">Delete</th>
                        </tr>
                    </thead>
                    <tbody>
                        {this.state.listBook}
                    </tbody>
                </table>
            </div>
        )
    }
}

export default ListBookTiki;
