import React from 'react'

class ListBookTiki extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            listBook: ''
        }
    }

    componentDidMount() {
        fetch('http://localhost:3600/books')
            .then(response => {
                return response.json()
            })
            .then(data => {
                // this.setState({ data })
                console.log(data)
                // let str = data.map ((row) => {
                //     return(
                //         <tr><th scope="row">1</th><td>{row.ID}</td><td>{row.Name}</td></tr>
                //     )

                // });
                let str2 = ''
                for (let i = 0; i < data.length; i++) {
                    const element = data[i];
                    str2 = str2 + <tr><th scope="row">{i}</th><td>{element.ID}</td><td>{element.Name}</td></tr>
                }

                this.setState({listBook: str2})
            });
    }

    render() {
        return (
            <div>
                <table class="table">
                    <thead>
                        <tr>
                            <th scope="col">#</th>
                            <th scope="col">ID</th>
                            <th scope="col">Name</th>
                        </tr>
                    </thead>
                    <tbody>
                        {this.state.listBook}
                    </tbody>
                </table>
            </div>


        )
    }
}

export default ListBookTiki;
