import ListBookTikiProduct from './ListBookTikiProduct'
import React from 'react'
import FontAwesome from 'react-fontawesome'
import {
    Link
} from 'react-router-dom';

import 'font-awesome/css/font-awesome.min.css';

class ListBookTiki extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            listBook: ''
        }
    }

    componentDidMount() {
        fetch('http://localhost:3600/books')
            .then(response => {
                return response.json()
            })
            .then(data => {
                // this.setState({ data })
                console.log(data)
                let str = data.map((row, index) => {
                    return (
                        <tr>
                            <th scope="row" style={{ textAlign: "center", verticalAlign: "middle" }}>{index + 1}</th>
                            <td> <Link to={"/listBookTikiProduct/" + row.ID}>{row.Name}</Link></td>
                            <td> 
                                <Link to={"/updateBook/" + row.ID + "/" + row.Name}>
                                    <svg viewBox="0 0 64 64" width="30" height="30" fill="currentcolor">
                                        <path d="M0 12v3h3l8-8-3-3-8 8zm3 2H1v-2h1v1h1v1zm10.3-9.3L12 6 9 3l1.3-1.3a.996.996 0 011.41 0l1.59 1.59c.39.39.39 1.02 0 1.41z" ></path>
                                    </svg>
                                </Link>
                            </td>
                            <td> 
                                <Link to={"/deleteBook/" + row.ID + "/" + row.Name}>
                                    <i className="fa fa-sun-o"/>
                                </Link>
                            </td>
                        </tr>
                    )
                });
                this.setState({ listBook: str })
            });
    }

    render() {
        return (
            <div>
                <table class="table">
                    <thead>
                        <tr>
                            <th scope="col" style={{ textAlign: "center" }}>STT</th>
                            <th scope="col">Name</th>
                            <th scope="col">Update</th>
                            <th scope="col">Delete</th>
                        </tr>
                    </thead>
                    <tbody>
                        {this.state.listBook}
                    </tbody>
                </table>
            </div>
        )
    }
}

export default ListBookTiki;
